import java.util.Arrays;// In selection sort, we compare starting element with every next element of the array then replace that index with the current index so the smallest value will come in the start or sorting mannerpublic class SelectionSort {    public static void main(String[] args) {        int[] arr= {5,4,3,2,1};        selectionSort(arr);        System.out.println(Arrays.toString(arr));    }    static void selectionSort(int[] arr){        for (int i = 0; i < arr.length; i++) {            int idx = i;       // Current index to sort the things            for (int j = i+1; j < arr.length; j++) {                if(arr[idx] > arr[j]){  // comparing and getting the maxIndex value to the current index to sort                    idx = j;                }            }            //swapping the values            int temp = arr[idx];            arr[idx] = arr[i];            arr[i] = temp;        }////        for (int i = 0; i < arr.length; i++) {//            System.out.print(arr[i]);////        }    }}