public class searchinrange{    public static void main(String[] args) {        int[] nums = {23,45,1,2,8,19,-1,16,-11,28};        int target = 199;        int start = 0;        int end = 5;        int ans = linearSearch(nums, target, start, end);        System.out.println(ans);    }    static int linearSearch(int[] arr, int target, int start, int end){        if(arr.length == 0){            return -1;        }        for(int index=start; index <= end; index++){            int element = arr[index];            if (element == target){                return index;            }        }        return -1;    }}